mapper.insert:
insert into token(token_id, nft_code, created_date, modified_date)
values (#{tokenId}, #{nftCode}, #{createdDate}, #{modifiedDate})

    select token_id
      from token
     where token_id = #{tokenId}
       and nft_code = #{nftCode}

mapper.select:
select token_id
from token
where token_id = #{tokenId}
